package conditions;

public class ifcondition3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=-13;
		if(num>0)
		{
			System.out.println("positive");
		}
		else if(num<0)
		{
			System.out.println("negative");
		}
		else
		{
			System.out.println("zero");
		}

	}

}
