package loops;

public class dowhile2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=1, times=0;
		do
		{
			System.out.println("java do while loop:" +n);
			n++;
		}
		while(n<=times);
		
		while(n<=times)
		{
			System.out.println("bye java:" +n);
			n++;
		}

	}

}
