package loops;

public class dowhileloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("print all the number 10 till 1");
		int c=10;
		do
		{
			System.out.println(c);
			c--;
		}
		while(c>0);

	}

}

